mapper.select:
SELECT a.id AS a_id, a.title AS a_title, a.content AS a_content, a.date AS a_date, a.article_type AS a_article_type, a.recommend AS a_recommend,
u.id AS id, u.age AS age, u.name AS name, u.birthday AS birthday, u.headshot AS headshot, u.signature AS signature, u.nickname AS nickname, u.phone AS phone, u.role AS role, u.love AS love, u.article_sum AS article_sum, u.address AS address, u.focus AS focus FROM article a INNER JOIN user u ON u.id = a.user_id
